<template>
  <div class="dashboard">
    <!-- Hero Metrics -->
    <div class="hero-metrics">
      <div class="metric">
        <span class="metric-label">Market Sentiment:</span>
        <span class="metric-value" :style="{ color: heroMetrics.market_sentiment.color }">
          {{ heroMetrics.market_sentiment.value }} ({{ heroMetrics.market_sentiment.change }})
        </span>
      </div>
      <div class="metric">
        <span class="metric-label">Active Signals:</span>
        <span class="metric-value">{{ heroMetrics.active_signals }}</span>
      </div>
      <div class="metric">
        <span class="metric-label">Opportunities:</span>
        <span class="metric-value">{{ heroMetrics.opportunities }}</span>
      </div>
      <div class="metric">
        <span class="metric-label">Risk Level:</span>
        <span class="metric-value" :class="{ 'risk-high': heroMetrics.risk_level === 'High', 'risk-medium': heroMetrics.risk_level === 'Medium', 'risk-low': heroMetrics.risk_level === 'Low' }">
          {{ heroMetrics.risk_level }}
        </span>
      </div>
    </div>

    <!-- Signal Cards -->
    <div class="signal-cards">
      <div v-for="signal in keySignals" :key="signal.symbol" class="signal-card">
        <div class="card-header" :style="{ backgroundColor: signalColor(signal.signal_type) }">
          {{ signal.symbol }} - {{ signal.signal_type.replace('_', ' ').toUpperCase() }}
        </div>
        <div class="card-body">
          <p class="card-what">{{ signal.what }}</p>
          <p class="card-why">{{ signal.why }}</p>
          <p class="card-so-what">{{ signal.so_what }}</p>
          <div class="card-footer">
            <span class="card-timeframe">{{ signal.timeframe }}</span>
            <span class="card-risk" :class="{ 'risk-high': signal.risk_level === 'high', 'risk-medium': signal.risk_level === 'medium', 'risk-low': signal.risk_level === 'low' }">
              Risk: {{ signal.risk_level.toUpperCase() }}
            </span>
          </div>
        </div>
      </div>
    </div>


    <!-- Market Themes -->
    <div class="market-themes">
      <h3>Market Themes</h3>
      <div v-for="theme in marketThemes" :key="theme.theme" class="theme-card">
        {{ theme.theme }} - {{ theme.impact.toUpperCase() }} ({{ theme.affected_stocks.join(', ') }})
      </div>
    </div>

    <!-- Risk Alerts -->
    <div class="risk-alerts">
      <h3>Risk Alerts</h3>
      <div v-for="alert in risks" :key="alert" class="alert-card">
        {{ alert }}
      </div>
    </div>

    <!-- Opportunities & Watchlist -->
    <div class="action-items">
      <h3>Opportunities</h3>
      <ul>
        <li v-for="opportunity in opportunities" :key="opportunity">{{ opportunity }}</li>
      </ul>
      <h3>Watchlist</h3>
      <ul>
        <li v-for="stock in watchlist" :key="stock">{{ stock }}</li>
      </ul>
    </div>
  </div>
</template>

<script>
export default {
  name: 'TradingDashboard',
  data() {
    return {
      heroMetrics: {
        market_sentiment: { value: 'Mixed', color: 'yellow', change: '0%' },
        active_signals: 3,
        opportunities: 2,
        risk_level: 'Medium'
      },
      keySignals: [
        //Data from the second JSON object populates here.  See computed property below
      ],
      marketThemes: [
        { theme: 'Tech Sector Consolidation', impact: 'neutral', affected_stocks: ['AAPL', 'MSFT', 'GOOG'] }
      ],
      risks: [
        "Global economic uncertainty",
        "Interest rate hikes"
      ],
      opportunities: [], //populated from data
      watchlist: [] //populated from data
    };
  },
  computed: {
    processedData() {
      //Process the second JSON data here
      return this.$store.state.dashboardData || {}; // Assuming you use Vuex for data
    },
    keySignals() {
        return this.processedData.key_signals || [];
    },
    opportunities() {
        return this.processedData.opportunities || [];
    },
    watchlist() {
        return this.processedData.watchlist || [];
    },
  },
  methods: {
    signalColor(signalType) {
      if (signalType.includes('bullish')) return 'green';
      if (signalType.includes('bearish')) return 'red';
      return 'yellow';
    }
  }
};
</script>

